using System;

namespace AdventOfCode02_1
{
    public class Program
    {
        public static void Main(string[] args)
        {
            string[] inputs = new string[] {
                "",
                "",
                "",
                "",
                ""
            };

            int X = 1, Y = 1;
            foreach(var instruction in inputs)
            {
                foreach(var letter in instruction)
                {
                    switch(letter)
                    {
                        case 'U':
                            Y -= 1;
                            break;
                        case 'D':
                            Y += 1;
                            break;
                        case 'L':
                            X -= 1;
                            break;
                        case 'R':
                            X += 1;
                            break;
                    }

                    X = (X > 2 ? 2 : (X < 0 ? 0 : X));
                    Y = (Y > 2 ? 2 : (Y < 0 ? 0 : Y));
                }
                Console.Write(Y * 3 + (X + 1));
            }
        }
    }
}
